import './App.css';
import React from "react";
import profile from "./images/zoro.png";

function App() {
  return (
    <div className="App">
      {/* Animated background with soft white lines */}
      <div className="background-lines"></div>

      <div className="content">
        {/* Left section: Text intro */}
        <div className="text-section">
          <h1>HELLO EVERYONE!</h1>
          <h2>MY NAME IS <span className="highlight">Ektha Mallya</span></h2>
        </div>

        {/* Right section: Profile image with subtle animation */}
        <div className="image-section">
          <img src={profile} alt="Ektha Mallya" className="profile-pic" />
        </div>
      </div>

    </div>
  );
}

export default App;
